    "code": "'use client'\n\nimport React from 'react'\n\nfunction PieChart({ data = [], size = 400, donut = false }) {\n  const canvasRef = React.useRef(null)\n  const [hoveredSlice, setHoveredSlice] = React.useState(null)\n\n  const colors = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899', '#06B6D4', '#84CC16']\n\n  React.useEffect(() => {\n    if (!data || data.length === 0) return\n\n    const canvas = canvasRef.current\n    const ctx = canvas.getContext('2d')\n    const centerX = size / 2\n    const centerY = size / 2\n    const radius = size / 2 - 40\n\n    ctx.clearRect(0, 0, size, size)\n\n    const total = data.reduce((sum, item) => sum + item.value, 0)\n    let currentAngle = -Math.PI / 2\n\n    data.forEach((item, i) => {\n      const sliceAngle = (item.value / total) * Math.PI * 2\n      const endAngle = currentAngle + sliceAngle\n\n      // Draw slice\n      ctx.fillStyle = item.color || colors[i % colors.length]\n      ctx.beginPath()\n      ctx.moveTo(centerX, centerY)\n      ctx.arc(centerX, centerY, radius, currentAngle, endAngle)\n      ctx.closePath()\n      ctx.fill()\n\n      // Highlight if hovered\n      if (hoveredSlice === i) {\n        ctx.strokeStyle = '#FFF'\n        ctx.lineWidth = 3\n        ctx.stroke()\n      }\n\n      // Label\n      const labelAngle = currentAngle + sliceAngle / 2\n      const labelRadius = radius + 20\n      const labelX = centerX + Math.cos(labelAngle) * labelRadius\n      const labelY = centerY + Math.sin(labelAngle) * labelRadius\n\n      ctx.fillStyle = '#374151'\n      ctx.font = '12px sans-serif'\n      ctx.textAlign = 'center'\n      ctx.fillText(`${item.label}`, labelX, labelY)\n\n      const percentage = ((item.value / total) * 100).toFixed(1)\n      ctx.fillText(`${percentage}%`, labelX, labelY + 14)\n\n      currentAngle = endAngle\n    })\n\n    // Donut hole\n    if (donut) {\n      ctx.fillStyle = '#FFF'\n      ctx.beginPath()\n      ctx.arc(centerX, centerY, radius * 0.6, 0, Math.PI * 2)\n      ctx.fill()\n\n      // Total in center\n      ctx.fillStyle = '#374151'\n      ctx.font = 'bold 24px sans-serif'\n      ctx.textAlign = 'center'\n      ctx.fillText(total, centerX, centerY)\n      ctx.font = '12px sans-serif'\n      ctx.fillText('Total', centerX, centerY + 20)\n    }\n\n  }, [data, size, donut, hoveredSlice])\n\n  const handleMouseMove = (e) => {\n    const canvas = canvasRef.current\n    const rect = canvas.getBoundingClientRect()\n    const x = e.clientX - rect.left\n    const y = e.clientY - rect.top\n\n    const centerX = size / 2\n    const centerY = size / 2\n    const dx = x - centerX\n    const dy = y - centerY\n    const distance = Math.sqrt(dx * dx + dy * dy)\n    const radius = size / 2 - 40\n\n    if (distance > radius || (donut && distance < radius * 0.6)) {\n      setHoveredSlice(null)\n      return\n    }\n\n    let angle = Math.atan2(dy, dx)\n    if (angle < -Math.PI / 2) angle += Math.PI * 2\n\n    const total = data.reduce((sum, item) => sum + item.value, 0)\n    let currentAngle = -Math.PI / 2\n    let found = null\n\n    data.forEach((item, i) => {\n      const sliceAngle = (item.value / total) * Math.PI * 2\n      const endAngle = currentAngle + sliceAngle\n\n      if (angle >= currentAngle && angle < endAngle) {\n        found = i\n      }\n\n      currentAngle = endAngle\n    })\n\n    setHoveredSlice(found)\n  }\n\n  return (\n    <div className=\"inline-block\">\n      <canvas\n        ref={canvasRef}\n        width={size}\n        height={size}\n        onMouseMove={handleMouseMove}\n        onMouseLeave={() => setHoveredSlice(null)}\n        className=\"cursor-pointer\"\n      />\n      {hoveredSlice !== null && (\n        <div className=\"mt-2 text-center text-sm text-gray-700 font-semibold\">\n          {data[hoveredSlice].label}: {data[hoveredSlice].value}\n        </div>\n      )}\n    </div>\n  )\n}\n\nexport default PieChart",
