    "code": "'use client'\n\nimport React from 'react'\n\nfunction Confetti({ duration = 3000, particleCount = 100 }) {\n  const canvasRef = React.useRef(null)\n  const [active, setActive] = React.useState(false)\n\n  const createConfetti = () => {\n    const canvas = canvasRef.current\n    const ctx = canvas.getContext('2d')\n    const particles = []\n\n    const colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#FFA07A', '#98D8C8', '#F7DC6F', '#BB8FCE']\n\n    for (let i = 0; i < particleCount; i++) {\n      particles.push({\n        x: Math.random() * canvas.width,\n        y: -20,\n        vx: (Math.random() - 0.5) * 8,\n        vy: Math.random() * 3 + 2,\n        rotation: Math.random() * 360,\n        rotationSpeed: (Math.random() - 0.5) * 10,\n        color: colors[Math.floor(Math.random() * colors.length)],\n        size: Math.random() * 8 + 4\n      })\n    }\n\n    const startTime = Date.now()\n\n    const animate = () => {\n      const elapsed = Date.now() - startTime\n      if (elapsed > duration) {\n        setActive(false)\n        return\n      }\n\n      ctx.clearRect(0, 0, canvas.width, canvas.height)\n\n      particles.forEach(p => {\n        p.x += p.vx\n        p.y += p.vy\n        p.vy += 0.2\n        p.rotation += p.rotationSpeed\n\n        ctx.save()\n        ctx.translate(p.x, p.y)\n        ctx.rotate(p.rotation * Math.PI / 180)\n        ctx.fillStyle = p.color\n        ctx.fillRect(-p.size / 2, -p.size / 2, p.size, p.size / 2)\n        ctx.restore()\n      })\n\n      requestAnimationFrame(animate)\n    }\n\n    animate()\n  }\n\n  React.useEffect(() => {\n    if (active) {\n      const canvas = canvasRef.current\n      canvas.width = window.innerWidth\n      canvas.height = window.innerHeight\n      createConfetti()\n    }\n  }, [active])\n\n  return (\n    <div className=\"relative\">\n      <button\n        onClick={() => setActive(true)}\n        className=\"bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white px-8 py-4 rounded-lg font-bold text-lg shadow-lg transform hover:scale-105 transition\"\n      >\n        üéâ Celebrate!\n      </button>\n\n      {active && (\n        <canvas\n          ref={canvasRef}\n          className=\"fixed inset-0 pointer-events-none z-50\"\n        />\n      )}\n    </div>\n  )\n}\n\nexport default Confetti",
